function nv = fineE(v)%% Interpola v, vettore in una griglia rada, in nv, vettore in una griglia% pi√π fine.%[nnc,mmc] = size(v);nc=nnc-1;mc=mmc-1;n=2*nc;m=2*mc;nn=n+1;mm=m+1;nv=zeros(nn,mm);for j=1:mmc    for i=1:nnc        ifine = 2*i-1;        jfine = 2*j-1;        nv(ifine,jfine) = v(i,j);    endendfor j=1:mmc    for i=1:nnc-1        ifine = 2*i-1;        jfine = 2*j-1;        nv(ifine+1,jfine) = ( nv(ifine+2,jfine) + nv(ifine,jfine) )/2;           endendfor i=1:nnc    for j=1:mmc-1        ifine = 2*i-1;        jfine = 2*j-1;            right = nv(ifine,jfine+2);            left = nv(ifine,jfine);              nv(ifine,jfine+1) = ( left + right )/2;           endendfor i=1:nnc-1    for j=1:mmc-1        ifine = 2*i-1;        jfine = 2*j-1;%             right = nv(ifine+1,jfine+2);%             up = nv(ifine+2,jfine+1);%             left = nv(ifine+1,jfine);%             down = nv(ifine,jfine+1);     %         nv(ifine+1,jfine+1) = ( up + down + left + right )/4;nv(ifine+1,jfine+1) = ( v(i,j) + v(i+1,j) + v(i,j+1) + v(i+1,j+1) )/4;    endend        